  font-size: 0;

}

#spa .container h1::before {

  display: block;

  content: 'Minha sacola';

  font-size: 2rem;

  color: var(--secondary);

  font-weight: 500;

}

.minicart__btn-cart {

  font-size: 0;

}

.minicart__btn-cart::after {

  content: 'Ver sacola';

  font-size: 1rem;

}

